package com.rootekstudio.repeatsandroid.database;import android.content.Context;import com.rootekstudio.repeatsandroid.RepeatsListDB;import com.rootekstudio.repeatsandroid.RepeatsSingleSetDB;import java.util.ArrayList;import java.util.List;import java.util.Random;public class SingleItemInfo {    private String question;    private String answer;    private String title;    private String pictureName;    private String ignoreChars;    public SingleItemInfo(Context context) {        getQuestionFromDatabase(context);    }    public SingleItemInfo(Context context, ArrayList<String> setsID) {        getAdvancedQuestionFromDatabase(context, setsID);    }    private void getAdvancedQuestionFromDatabase(Context context, ArrayList<String> setsID) {        String choosenSetID;        int count = setsID.size();        if (count == 1) {            choosenSetID = setsID.get(0);        } else {            Random random = new Random();            int randomint = random.nextInt(count);            choosenSetID = setsID.get(randomint);        }        DatabaseHelper DB = new DatabaseHelper(context);        RepeatsListDB singleTitle = DB.getSingleItemLIST(choosenSetID);        title = singleTitle.getitle();        ignoreChars = singleTitle.getIgnoreChars();        List<RepeatsSingleSetDB> allQuestions = DB.AllItemsSET(choosenSetID);        int allQcount = allQuestions.size();        RepeatsSingleSetDB single;        if (allQcount == 1) {            single = allQuestions.get(0);        } else {            Random randomset = new Random();            int randomsetint = randomset.nextInt(allQcount);            single = allQuestions.get(randomsetint);        }        question = single.getQuestion();        answer = single.getAnswer();        pictureName = single.getImag();    }    private void getQuestionFromDatabase(Context context) {        DatabaseHelper DB = new DatabaseHelper(context);        List<RepeatsListDB> all = DB.ALLEnabledSets();        int count = all.size();        if(count == 0){            return;        }        Random random = new Random();        int randomint = random.nextInt(count);        RepeatsListDB single = all.get(randomint);        title = single.getitle();        String Title = single.getTableName();        ignoreChars = single.getIgnoreChars();        List<RepeatsSingleSetDB> set = DB.AllItemsSET(Title);        int setcount = set.size();        Random randomset = new Random();        int randomsetint = randomset.nextInt(setcount);        RepeatsSingleSetDB singleSetDB = set.get(randomsetint);        question = singleSetDB.getQuestion();        answer = singleSetDB.getAnswer();        pictureName = singleSetDB.getImag();    }    public String getQuestion() {        return question;    }    public String getAnswer() {        return answer;    }    public String getTitle() {        return title;    }    public String getPictureName() {        return pictureName;    }    public String getIgnoreChars() {        return ignoreChars;    }}